select 
		"doc".BatNbr,"doc".RefNbr,"doc".User2,"doc".User6 
		,"xwar".*
from 
		sltestapp.dbo.ARdoc as "doc"
	inner join 
		sltestapp.dbo.XGRWARTrans as "xwar"
	on 
		"doc".BatNbr = "xwar".BatNbr and "doc".RefNbr = "xwar".RefNbr
where 
	"doc".BatNbr = '065276' and "doc".RefNbr = '050960'
	
	
